deploy.preDeployCommand:
/bin/bash -lc "python manage.py migrate && python manage.py shell -c 'import os; from django.contrib.auth import get_user_model; username = os.environ.get("DJANGO_SUPERUSER_USERNAME"); password = os.environ.get("DJANGO_SUPERUSER_PASSWORD"); email = os.environ.get("DJANGO_SUPERUSER_EMAIL");
if not username or not password:
    print("Superuser variables missing");
else:
    if not email:
        email = f"{username}@example.com"
    User = get_user_model()
    defaults = {"email": email, "is_staff": True, "is_superuser": True, "is_active": True}
    if any(f.name == "role" for f in User._meta.fields):
        defaults["role"] = "admin"
    user, created = User.objects.update_or_create(username=username, defaults=defaults)
    user.set_password(password)
    user.save()
    print("Superuser ensured", "created" if created else "updated")'"
