python manage.py migrate && python manage.py shell -c "from django.contrib.auth import get_user_model; import os; User = get_user_model(); username = os.environ.get('DJANGO_SUPERUSER_USERNAME'); password = os.environ.get('DJANGO_SUPERUSER_PASSWORD'); email = os.environ.get('DJANGO_SUPERUSER_EMAIL', '');
if username and password:
    user, created = User.objects.get_or_create(username=username);
